import {
	ADD_TO_CART,
	DECREASE_CART,
	REMOVE_FROM_CART,
} from "../Constants/types";

export const cartReducer = (
	state = {
		cartItems: JSON.parse(localStorage.getItem("cartItems") || "[]"),
	},
	action
) => {
	switch (action.type) {
		case ADD_TO_CART:
			return { cartItems: action.payload.cartItems };
		case REMOVE_FROM_CART:
			return { cartItems: action.payload.cartItems };
		case DECREASE_CART:
			return { cartItems: action.payload.cartItems };
		default:
			return state;
	}
};
